function valores(num1, num2) {
    let soma = num1 + num2;
    let sub = num1 - num2;
    let mult = num1 * num2;
    let divi = num1 / num2;

    console.log("Soma: " + soma);
    console.log("Subtração: " + sub);
    console.log("Munltiplicação: " + mult);
    console.log("Divisão: " + divi);

}

valores(10, 5);